mapper.select:
select 

from t_account_master m left join t_account t on m.ACCOUNT_ID=t.ID where
t.REAL_ACCOUNT_NUMBER=#{realAcctNum}

mapper.update:
update t_account_master SET
ADDRESS = #{address}, CONTACT_ADDRESS = #{contactAddress}, CONTACT_NUMBER = #{contactNumber}, WECHAT_ID = #{wechatId}
where ACCOUNT_ID = #{accountId}
